function sendEmail($subject,$content){
	$toEmail = 'c.clewley@imperial.ac.uk';
	$toName = 'Dr Caroline Clewley';

	$message = '
<!doctype html><html> <head><meta charset="utf-8"><meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no"><title>'.$subject.'</title><style> @import url("https://fonts.googleapis.com/css?family=Fira+Sans:300,400,500,600,700&amp;subset=greek"); html,* { margin: 0;padding: 0;} body {font-size: 1.0em; line-height: 1.6; font-weight: 400; font-family: "Fira Sans", "HelveticaNeue", "Helvetica Neue", Helvetica, Arial, Calibri, sans-serif; color: #222; margin-top: 60px; }#title{line-height: 1em;margin-bottom: 0.5em;margin-top: 0.2em;} .center{margin: 0 auto; display: block; box-sizing: border-box; width: 100%; padding: 0 10px; }#navbar{height: 60px; width: 100%; background-color: #003E74; position: fixed; top: 0; left: 0px; text-align: center; }@media (min-width: 400px) {.center{width: 400px; }#navbar{text-align: left; }} @media (min-width: 800px) {.center{width: 700px; }}  #navbar>a>{text-decoration: none; color: #fff; } #navbar>a>img{margin: 12px 0px; }small{display: block;margin-bottom: 1em;}</style></head><body> <div id="navbar" href="#"> <a href="https://visualisations.ph.ic.ac.uk/" class="center"><img src="data:image/svg+xml;base64," height="36" alt=""></a> </div> <div class="center section"><h1 id="title">'.$subject.'</h1>Dear '.$toName.',<br><small></small>'.$content.'<small></small>Imperial Visualisations CMS<br><br><small>This is an system-generated email. Please do not reply directly as this email address does not exist. If you have any problems please contact Mangle Kuo (hao-tsun.kuo15@imperial.ac.uk)</small> <center>Imperial Visualisations &copy; MMXVII</center> </div> </body> </html>	';

	// To send HTML mail, the Content-type header must be set
	$headers[] = 'MIME-Version: 1.0';
	$headers[] = 'Content-type: text/html; charset=iso-8859-1';

	// Additional headers
	$headers[] = 'From: Imperial Visualisations CMS <visualisations@imperial.ac.uk>';

	// Mail it
	mail($toEmail, $subject, $message, implode("\r\n", $headers));
}
